meta.script:
$(document).ready(function(){
    if ($("#codeblob tr td.line-numbers:last").text().length < 3500) {
        prettyPrint();
    } else {
        $("#long-file").show().find("a#highlight-anyway").click(function(e){
            prettyPrint();
            e.preventDefault();
        });
    }
});

